// Get message and phone
const webhookBody = $input.item.json.body || $input.item.json;
const message = (webhookBody.body?.data?.message?.conversation || webhookBody.data?.message?.conversation || '').trim().toLowerCase();
const phone = webhookBody.body?.data?.key?.remoteJid || webhookBody.data?.key?.remoteJid;

// Get current state from static data (conversation memory)
const workflowStatic = this.getWorkflow().staticData || {};
if (!workflowStatic.conversations) {
  workflowStatic.conversations = {};
}

const userState = workflowStatic.conversations[phone] || { step: 'idle' };

// Handle conversation flow
let response = '';
let shouldSave = false;
let expenseData = null;

if (message === 'add expense' || message === 'add') {
  userState.step = 'waiting_category';
  response = 'üìù What category?\n\nExamples: food, transport, bills, shopping, entertainment, health';

} else if (userState.step === 'waiting_category') {
  userState.category = message;
  userState.step = 'waiting_amount';
  response = `üí∞ How much? (just enter the number)`;

} else if (userState.step === 'waiting_amount') {
  const amount = parseFloat(message.replace(/,/g, ''));
  if (isNaN(amount)) {
    response = '‚ùå Invalid amount. Please enter a number (e.g., 25 or 150.50)';
  } else {
    userState.amount = amount;
    userState.step = 'waiting_description';
    response = `üìã Description? (e.g., lunch, taxi, groceries)`;
  }

} else if (userState.step === 'waiting_description') {
  userState.description = message;
  shouldSave = true;
  expenseData = {
    category: userState.category,
    amount: userState.amount,
    description: userState.description,
    phone: phone
  };
  // Reset state
  userState.step = 'idle';
  response = 'saving';

} else {
  response = 'üëã Hi! Send "add expense" to start tracking an expense.\n\nOr use quick format: expense description amount category\nExample: expense lunch 25 food';
}

// Save state
workflowStatic.conversations[phone] = userState;

return {
  response: response,
  shouldSave: shouldSave,
  expenseData: expenseData,
  phone: phone
};

const expense = $('Save to Database').item.json;
const monthlyData = $('Get Monthly Total').item.json;

const reply = `‚úÖ Expense added!
üí∞ Amount: RM${expense.amount}
üè∑Ô∏è Description: ${expense.description}
üóÇÔ∏è Category: ${expense.category}

üìä This Month Total:
üíµ RM${monthlyData.total} (${monthlyData.count} expenses)`;

return { reply: reply };
