// Get the message and phone number from the correct path
const webhookBody = $input.item.json.body || $input.item.json;
const message = webhookBody.body?.data?.message?.conversation || webhookBody.data?.message?.conversation;
const phone = webhookBody.body?.data?.key?.remoteJid || webhookBody.data?.key?.remoteJid;

if (!message) {
  throw new Error('No message found in webhook data');
}

// Parse the message: "expense description amount category"
const parts = message.split(' ');

if (parts[0].toLowerCase() !== 'expense' || parts.length < 3) {
  throw new Error('Invalid format. Use: expense description amount category');
}

const description = parts[1];
const amount = parseFloat(parts[2].replace(/,/g, ''));
const category = parts[3] || 'general';

return {
  description: description,
  amount: amount,
  category: category,
  phone: phone
};

// Build the response message
