// Get the message and phone number
const message = $input.item.json.body.data.message.conversation;
const phone = $input.item.json.body.data.key.remoteJid;

// Parse the message: "expense description amount category"
const parts = message.split(' ');

if (parts[0].toLowerCase() !== 'expense' || parts.length < 3) {
  return { error: 'Invalid format. Use: expense description amount category' };
}

const description = parts[1];
const amount = parseFloat(parts[2].replace(/,/g, ''));
const category = parts[3] || 'general';

return {
  description: description,
  amount: amount,
  category: category,
  phone: phone
};
